// try to locate one first:        Registry reg = LocateRegistry.getRegistry( m_port );        boolean valid = false;        if( reg != null ) {            // test:            try {                String[] objectsRemote = reg.list();                for( String r : objectsRemote ) {                    LOG.warn( "-- Remotely available already: " + r );                }                valid = true;            } catch( Exception ex ) {                //            }        }        if( !valid ) {            LOG.info( "Create new registry: " + m_port );            LocateRegistry.createRegistry( m_port );        }        else {            LOG.info( "Reuse registry: " + m_port );        }    }    /**     * {@inheritDoc}     */    public TestContainer[] parse( Option... options )    {        try {            options = expand( combine( options, setDefaultOptions() ) );            List<TestContainer> containers = new ArrayList<TestContainer>();            FrameworkOption[] frameworks = ( filter( FrameworkOption.class, options ) );            if( frameworks.length == 0 ) {                frameworks = new FrameworkOption[]{ felix() };            }            options = remove( FrameworkOption.class, options );            for( FrameworkOption framework : frameworks ) {                String containerName = UUID.randomUUID().toString();                RBCRemoteTarget target = new RBCRemoteTarget( m_port, containerName, RMI_LOOKUP_TIMEOUT );                ArgumentsBuilder argBuilder = new ArgumentsBuilder( containerName, m_host, m_port, combine( options, framework ) );                containers.add( new PaxRunnerTestContainer( new DefaultJavaRunner( false ),                                                            argBuilder.getArguments(),                                                            argBuilder.getWorkingFolder(),                                                            argBuilder.getStartTimeout(),                                                            target,                                                            argBuilder.getCustomizers()                )                );            }            return containers.toArray( new TestContainer[ containers.size() ] );        } catch( IOException e ) {            throw new TestContainerException( "Problem parsing options", e );        }    }    public void shutdown()    {        System.gc();    }    private Option[] setDefaultOptions()    {        return new Option[]{            // remote bundle context bundle            // rmi communication port            //,            // boot delegation for sun.*. This seems only necessary in Knopflerfish version > 2.0.0            bootDelegationPackage( "sun.*" ),            url( "link:classpath:META-INF/links/org.ops4j.pax.exam.rbc.link" ),            url( "link:classpath:META-INF/links/org.ops4j.pax.extender.service.link" ),            url( "link:classpath:META-INF/links/org.osgi.compendium.link" ),            url( "link:classpath:META-INF/links/org.ops4j.pax.logging.api.link" )        };    }}